	@GetMapping(value = "/createUreaRequisition")
	public ModelAndView createUreaRequisition(final HttpServletRequest request, final HttpServletResponse response) {
		Map<String, Object> 		model 						= null;
		CustomUserDetails			userDetails					= null;
		try {
			userDetails		= (CustomUserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();
			model 		= new HashMap<String, Object>();
			model.put("requisitionSender", userDetails.getFirstName());
			
		} catch (Exception e) {
			e.printStackTrace();
			e.printStackTrace();
		}
		return new ModelAndView("createUreaRequisition", model);
	}
	
