	@GetMapping(value = "/showUreaRequisitionDetail")
	public ModelAndView showUreaRequisition(@RequestParam("Id") final Long ureaRequisitionId, final HttpServletRequest request, final HttpServletResponse response) {
		Map<String, Object> 		model 				= null;
		CustomUserDetails			userDetails					= null;
		ValueObject					valueObject			= null;
		try {
			valueObject = new ValueObject();
			userDetails			= (CustomUserDetails) SecurityContextHolder.getContext().getAuthentication().getPrincipal();
			model 				= new HashMap<String, Object>();
			model.put("userId", userDetails.getId());
			model.put("ureaRequisitionId", ureaRequisitionId);
			valueObject.put("ureaRequisitionId", ureaRequisitionId);
			valueObject = ureaRequisitionService.getRequisitionDetailById(valueObject);
			if(valueObject.getBoolean("notFound")) {
				return new ModelAndView("ureaRequisitionAndTransferDetails", model);
			}

		} catch (Exception e) {
			e.printStackTrace();
			e.printStackTrace();
		}
		return new ModelAndView("showUreaRequisitionDetail", model);
	}
	
